__NUXT_JSONP__("/wiki/%E8%B7%91%E9%9D%A2", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t){return {data:[{WikiData:{slug:s,description:t,title:s,introduction:"跑面，是一个汉语词汇，拼音为pǎo miàn，英文名为Run-Noodles，最基本解释为人跑步去吃面，其意义还有多重深层解释。\n",info:{"中文名":s,"外文名":"Run-Noodles","别称":"跑面日，一二·一二跑面日","时间":"每年12月12日12点12分12秒","地点":"厦门市音乐学校"},toc:[{id:m,depth:e,text:m},{id:n,depth:e,text:n},{id:o,depth:e,text:o},{id:p,depth:e,text:p},{id:q,depth:e,text:q}],body:{type:"root",children:[{type:a,tag:f,props:{id:m},children:[{type:a,tag:g,props:{ariaHidden:h,href:"#%E5%9F%BA%E6%9C%AC%E5%90%AB%E4%B9%89",tabIndex:i},children:[{type:a,tag:j,props:{className:[k,l]},children:[]}]},{type:b,value:m}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:t}]},{type:b,value:c},{type:a,tag:r,props:{},children:[]},{type:b,value:c},{type:a,tag:f,props:{id:n},children:[{type:a,tag:g,props:{ariaHidden:h,href:"#%E5%AE%8C%E6%95%B4%E9%87%8A%E4%B9%89",tabIndex:i},children:[{type:a,tag:j,props:{className:[k,l]},children:[]}]},{type:b,value:n}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:"跑面指人在一定的时间从一定的地点拼尽全力，在途中跨越重重障碍，跑步到另一可以吃到面的地点的整个过程。"}]},{type:b,value:c},{type:a,tag:r,props:{},children:[]},{type:b,value:c},{type:a,tag:f,props:{id:o},children:[{type:a,tag:g,props:{ariaHidden:h,href:"#%E4%BA%A7%E7%94%9F%E8%BF%87%E7%A8%8B",tabIndex:i},children:[{type:a,tag:j,props:{className:[k,l]},children:[]}]},{type:b,value:o}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:"在 2018 年 12 月 12 日，学生布莱恪（Blacque）、必胜黑恪（BestHeaque）在 12 月 12 日的 11 点 45 分从他们所在的七年八班经历了重重困难，躲过了老师的注意，跑步到食堂来排队打清汤面（砂锅），可是那时来吃面的学生很多，他们终于在 12 点 12 分打到了一碗热腾腾的清汤面（砂锅），布莱恪和必胜黑恪望着他们用尽了全力才打来的面，有感而发，将这整个过程命名为”跑面“。"}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:"又因其时间（12 月 12 日 12 点 12 分 12 秒）的特殊性，决定将这一天定为“跑面日”，又称“一二·一二跑面日”。并规定每年的 12 月 12 日，恪人族的全体成员将集体跑步去吃面。"}]},{type:b,value:c},{type:a,tag:r,props:{},children:[]},{type:b,value:c},{type:a,tag:f,props:{id:p},children:[{type:a,tag:g,props:{ariaHidden:h,href:"#%E7%AC%AC%E4%BA%8C%E6%AC%A1%E5%85%A8%E4%BD%93%E8%B7%91%E9%9D%A2",tabIndex:i},children:[{type:a,tag:j,props:{className:[k,l]},children:[]}]},{type:b,value:p}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:"在 2019 年 12 月 12 日，自第一次跑面成功以来，恪人族成员又开始了第二次集体跑面，在 11 点 45 分时，恪人族成员影恪做了一块纸板，上面写着“Run-Noodles；12,12,12,12,12”，很快，全体恪人族成员出发了，仿佛是巧合一样，他们又正好在 12 点 12 分达到了清汤面，当他们端着自己辛辛苦苦打来的面时，许多人不禁赞叹，并同意加入恪人族跑面活动，我们也期待在 2020 年的 12 月 12 日也能再创辉煌，跑面成功!"}]},{type:b,value:c},{type:a,tag:r,props:{},children:[]},{type:b,value:c},{type:a,tag:f,props:{id:q},children:[{type:a,tag:g,props:{ariaHidden:h,href:"#%E5%BB%B6%E4%BC%B8%E6%84%8F%E4%B9%89",tabIndex:i},children:[{type:a,tag:j,props:{className:[k,l]},children:[]}]},{type:b,value:q}]},{type:b,value:c},{type:a,tag:d,props:{},children:[{type:b,value:"现代人们经常用”跑面“来称赞那些做事细致认真，为了目标而不惜一切，拼尽全力，为目标而奋斗的人。"}]}]},dir:"\u002Fwiki",path:"\u002Fwiki\u002F跑面",extension:".md",createdAt:"2022-07-08T10:06:54.347Z",updatedAt:"2022-07-09T08:34:53.305Z"},metaTitle:"跑面 [耳斯百科]"}],fetch:{},mutations:void 0}}("element","text","\n","p",2,"h2","a","true",-1,"span","icon","icon-link","基本含义","完整释义","产生过程","第二次全体跑面","延伸意义","hr","跑面","最基本的含义指人跑步去吃面，泛指整个过程。")));